const output = $input.first().json.output || "";  // Your AI raw output string

// Simple helper to extract with regex safely
function extract(regex) {
  const match = output.match(regex);
  return match ? match[1].trim() : "";
}

// Extract title, description, tags from AI output
const title = extract(/##?\s*Video Title\s*\n\*\*(.+?)\*\*/i);
const description = extract(/##?\s*Video Description\s*\n([\s\S]+?)\n---/i);
const tagsRaw = extract(/##?\s*SEO Tags\s*\n([\s\S]+)/i);

// Convert tags list to comma-separated string
let tags = tagsRaw
  .split(/[\n,]+/)      // split by newline or comma
  .map(t => t.trim())   // trim spaces
  .filter(t => t.length > 0);
tags = tags.join(", ");

// Build nicely formatted plain text content for Google Docs
const doc_content = 
`YouTube Video SEO Details

Video Title
${title}

Video Description
${description}

SEO Tags
${tags}
`;

return [{
  json: {
    title,
    description,
    tags,
    doc_content
  }
}];

